import React, { useEffect, useMemo, useState } from 'react';
import { addEvent, listEvents, deleteEvent } from '../lib/calendar';
import Modal from '../components/Modal';
import EventTypeSwitcher from '../components/EventTypeSwitcher';
import ViewSwitcher from '../components/ViewSwitcher';
import MonthlyCalendarView from '../components/MonthlyCalendarView';
import CollapsibleSection from '../components/CollapsibleSection';
import HeartRainAnimation from '../components/HeartRainAnimation';

const EventList = ({ events, onDelete }) => {
  if (events.length === 0) {
    return <div className="text-gray-500 text-sm px-4 py-2">No hay eventos aquÃ­.</div>;
  }
  return (
    <ul className="divide-y divide-rose-100">
      {events.map((ev) => {
        const startDate = ev.start?.toDate?.() || null;
        const endDate = ev.end?.toDate?.() || null;

        let when = '';
        if (startDate) {
          const startStr = startDate.toLocaleString([], { dateStyle: 'medium', timeStyle: 'short' });
          if (endDate && endDate.toDateString() !== startDate.toDateString()) {
            // Multi-day event: show date range
            const endStr = endDate.toLocaleString([], { dateStyle: 'medium', timeStyle: 'short' });
            when = `${startStr} - ${endStr}`;
          } else {
            // Single day event
            when = startStr;
          }
        }

        // Get event type color
        let eventTypeColor = 'bg-rose-500'; // Default: conjunto (pink)
        if (ev.eventType === 'novio') {
          eventTypeColor = 'bg-yellow-500';
        } else if (ev.eventType === 'novia') {
          eventTypeColor = 'bg-purple-500';
        }

        return (
          <li key={ev.id} className="py-3 flex items-center gap-3">
            <div className="flex-1 min-w-0">
              <div className="font-medium text-gray-900 truncate flex items-center gap-2">
                <span className="truncate">{ev.title}</span>
                <div className={`w-2 h-2 rounded-full flex-shrink-0 ${eventTypeColor}`}></div>
              </div>
              <div className="text-sm text-gray-500 truncate">{when}{ev.location ? ` Â· ${ev.location}` : ''}</div>
            </div>
            <button onClick={() => onDelete(ev.id)} className="btn-link text-sm">Borrar</button>
          </li>
        );
      })}
    </ul>
  );
};

export default function CalendarPage() {
  const [view, setView] = useState('Lista'); // 'Lista' | 'Calendario'
  const [isModalOpen, setIsModalOpen] = useState(false);
  const pairId = useMemo(() => localStorage.getItem('pairId') || '', []);
  const identity = useMemo(() => localStorage.getItem('identity') || 'yo', []);
  const [items, setItems] = useState([]);
  const [loading, setLoading] = useState(true);
  const [refreshKey, setRefreshKey] = useState(0);
  const [saving, setSaving] = useState(false);
  const [error, setError] = useState('');
  const [selectedEventType, setSelectedEventType] = useState('conjunto');
  const [selectedDay, setSelectedDay] = useState(null);
  const [dayEventsPopup, setDayEventsPopup] = useState(false);
  const [showHeartRain, setShowHeartRain] = useState(false);
  const [heartAnimationType, setHeartAnimationType] = useState('rain');

  useEffect(() => {
    let cancelled = false;
    (async () => {
      setLoading(true);
      try {
        const list = await listEvents(pairId, { futureOnly: false, max: 100 });
        if (!cancelled) setItems(list);
      } catch (e) {
        if (!cancelled) setError('Sin conexiÃ³n o sin permisos.');
      } finally {
        if (!cancelled) setLoading(false);
      }
    })();
    return () => { cancelled = true; };
  }, [pairId, refreshKey]);

  const { upcomingEvents, pastEvents } = useMemo(() => {
    const now = new Date();
    const upcoming = [];
    const past = [];
    const sortedItems = [...items].sort((a,b) => (a.start?.toMillis?.()||0) - (b.start?.toMillis?.()||0));

    for (const event of sortedItems) {
      if ((event.start?.toDate() || 0) >= now) {
        upcoming.push(event);
      } else {
        past.push(event);
      }
    }
    return { upcomingEvents: upcoming, pastEvents: past.reverse() };
  }, [items]);

  async function onAdd(e) {
    e.preventDefault();
    const { title, location, date, time, endDate } = Object.fromEntries(new FormData(e.currentTarget));
    if (!title || !date) return;
    setSaving(true);
    setError('');

    // Call addEvent and close popup regardless of result
    addEvent(pairId, { title, date, time, endDate, location, eventType: selectedEventType }, identity);

    e.currentTarget.reset();
    setSelectedEventType('conjunto'); // Reset to default
    setIsModalOpen(false);
    setSaving(false);
    setRefreshKey(k => k + 1); // Force a reliable refetch
  }

  const onDelete = async (id) => {
    if (!window.confirm('Â¿Borrar evento?')) return;
    try {
      await deleteEvent(pairId, id);
      setRefreshKey(k => k + 1);
    } catch (e) {
      alert('Error al borrar');
    }
  };

  const onDayClick = (day, month, year) => {
    setSelectedDay({ day, month, year });
    setDayEventsPopup(true);

    // Special celebration for day 24 (anniversary day)
    if (day === 24) {
      setShowHeartRain(true);
      // Stop animation after 2 seconds
      setTimeout(() => setShowHeartRain(false), 2000);
    }
  };

  return (
    <div className="space-y-4">
      <ViewSwitcher 
        views={['Lista', 'Calendario']}
        activeView={view}
        onChange={setView}
      />

      {view === 'Calendario' && <MonthlyCalendarView events={items} onDayClick={onDayClick} />}

      {view === 'Lista' && (
        <div className="relative min-h-[60vh]">
          <div className="divide-y divide-gray-200">
            <div className="card rounded-b-none">
              <CollapsibleSection title="PrÃ³ximos eventos" defaultOpen>
                {loading ? <div className="text-gray-500 px-4 py-2">Cargandoâ€¦</div> : <EventList events={upcomingEvents} onDelete={onDelete} />}
              </CollapsibleSection>
            </div>
            <div className="card rounded-t-none">
              <CollapsibleSection title="Eventos pasados">
                <EventList events={pastEvents} onDelete={onDelete} />
              </CollapsibleSection>
            </div>
          </div>
          <button onClick={() => setIsModalOpen(true)} className="fixed bottom-24 right-4 bg-rose-500 text-white rounded-full px-5 py-3 font-semibold shadow-lg hover:bg-rose-600 transition-transform active:scale-95">
            Nuevo evento
          </button>
        </div>
      )}

      <Modal isOpen={isModalOpen} onClose={() => setIsModalOpen(false)}>
        <form onSubmit={onAdd} className="p-6 space-y-4">
          <h3 className="font-semibold text-lg">AÃ±adir evento</h3>
          <div className="space-y-3">
            <input name="title" placeholder="TÃ­tulo" className="input w-full" required />
            <input name="location" placeholder="UbicaciÃ³n (opcional)" className="input w-full" />
            <div className="grid grid-cols-2 gap-3">
              <input name="date" type="date" className="input w-full" required />
              <input name="time" type="time" className="input w-full" />
            </div>
            <input name="endDate" type="date" className="input w-full" />
            <div className="space-y-2">
              <label className="block text-sm text-gray-600">Tipo de evento</label>
              <EventTypeSwitcher 
                activeType={selectedEventType} 
                onChange={setSelectedEventType} 
              />
            </div>
          </div>
          {error && <p className="text-xs text-rose-600">{error}</p>}
          <div className="flex justify-end gap-2">
            <button type="button" onClick={() => setIsModalOpen(false)} className="btn-ghost">Cancelar</button>
            <button disabled={saving} className="btn-primary disabled:opacity-60">{saving ? 'Guardandoâ€¦' : 'AÃ±adir'}</button>
          </div>
        </form>
      </Modal>

      <Modal isOpen={dayEventsPopup} onClose={() => setDayEventsPopup(false)}>
        <div className="p-6">
          <h3 className="font-semibold text-lg mb-4">
            {selectedDay && `Eventos del ${selectedDay.day} de ${new Date(selectedDay.year, selectedDay.month).toLocaleDateString('es-ES', { month: 'long', year: 'numeric' })}`}
          </h3>
          {selectedDay && (() => {
            // Special anniversary message for day 24
            const isAnniversaryDay = selectedDay.day === 24;
            let anniversaryMessage = null;

            if (isAnniversaryDay) {
              const anniversaryDate = new Date(2024, 10, 24); // November 24, 2024 (month is 0-indexed)
              const selectedDate = new Date(selectedDay.year, selectedDay.month, selectedDay.day);

              // Calculate months difference
              const yearsDiff = selectedDate.getFullYear() - anniversaryDate.getFullYear();
              const monthsDiff = selectedDate.getMonth() - anniversaryDate.getMonth();
              const totalMonths = yearsDiff * 12 + monthsDiff;

              if (totalMonths > 0) {
                let message = '';
                if (totalMonths >= 12) {
                  const years = Math.floor(totalMonths / 12);
                  const remainingMonths = totalMonths % 12;
                  if (remainingMonths === 0) {
                    message = `Â¡Â¡${years} ${years === 1 ? 'aÃ±o' : 'aÃ±os'}!!`;
                  } else {
                    message = `Â¡Â¡${years} ${years === 1 ? 'aÃ±o' : 'aÃ±os'} y ${remainingMonths} ${remainingMonths === 1 ? 'mes' : 'meses'}!!`;
                  }
                } else {
                  message = `Â¡Â¡${totalMonths} ${totalMonths === 1 ? 'mes' : 'meses'}!!`;
                }

                anniversaryMessage = (
                  <div className="bg-gradient-to-r from-pink-50 to-rose-50 border border-pink-200 rounded-lg p-4 mb-4 text-center">
                    <div className="text-2xl mb-2">ðŸ’–</div>
                    <div className="text-lg font-semibold text-pink-600 mb-1">
                      {message}
                    </div>
                    <div className="text-sm text-pink-500">
                      Â¡Feliz mesiversario!
                    </div>
                  </div>
                );
              }
            }

            // Filter events for the selected day
            const dayEvents = items.filter(event => {
              if (!event.start?.toDate) return false;
              const eventStart = event.start.toDate();
              const eventEnd = event.end?.toDate() || eventStart;
              const selectedDate = new Date(selectedDay.year, selectedDay.month, selectedDay.day);

              // Check if the selected date falls within the event range
              return selectedDate >= new Date(eventStart.getFullYear(), eventStart.getMonth(), eventStart.getDate()) &&
                     selectedDate <= new Date(eventEnd.getFullYear(), eventEnd.getMonth(), eventEnd.getDate());
            });

            return (
              <>
                {anniversaryMessage}
                {dayEvents.length > 0 ? (
                  <EventList events={dayEvents} onDelete={onDelete} />
                ) : (
                  !anniversaryMessage && <div className="text-gray-500 text-sm py-4">No hay eventos en este dÃ­a.</div>
                )}
              </>
            );
          })()}
          <div className="flex justify-end mt-4">
            <button onClick={() => setDayEventsPopup(false)} className="btn-primary">Cerrar</button>
          </div>
        </div>
      </Modal>

      <HeartRainAnimation isActive={showHeartRain} duration={2000} />
    </div>
  );
}
