import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { listPhotos } from '../lib/photos';

export default function RandomPhoto() {
  const [photo, setPhoto] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    let isCancelled = false;
    async function fetchRandomPhoto() {
      try {
        const pairId = localStorage.getItem('pairId');
        if (!pairId) {
          setLoading(false);
          return;
        }
        const photos = await listPhotos(pairId);
        if (!isCancelled && photos.length > 0) {
          const randomIndex = Math.floor(Math.random() * photos.length);
          setPhoto(photos[randomIndex]);
        }
      } catch (error) {
        console.error("Error fetching random photo:", error);
      } finally {
        if (!isCancelled) {
          setLoading(false);
        }
      }
    }

    fetchRandomPhoto();
    return () => { isCancelled = true; };
  }, []);

  if (loading) {
    return (
      <div className="card">
        <div className="text-sm text-gray-600 mb-2">Foto del día</div>
        <div className="aspect-square bg-gray-200 rounded-lg"></div>
      </div>
    );
  }

  if (!photo) {
    return null; // Don't show anything if there are no photos
  }

  return (
    <div className="card">
      <div className="flex items-center justify-between mb-2">
        <h3 className="text-sm text-gray-600">Foto del día</h3>
        <Link to="/gallery" className="btn-link text-sm">Ver galería</Link>
      </div>
      <Link to={`/gallery?photo=${photo.id}`} className="block w-full aspect-square bg-gray-100 rounded-lg overflow-hidden">
        <img 
          src={photo.thumbUrl}
          alt="Recuerdo aleatorio"
          className="w-full h-full object-cover hover:scale-105 transition-transform duration-200"
        />
      </Link>
    </div>
  );
}
