import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { listPhotos } from '../lib/photos';

export default function RandomPhoto() {
  const [photo, setPhoto] = useState(null);
  const [loading, setLoading] = useState(true);
  const [imageLoaded, setImageLoaded] = useState(false);

  useEffect(() => {
    let isCancelled = false;
    async function fetchRandomPhoto() {
      try {
        const pairId = localStorage.getItem('pairId');
        if (!pairId) {
          setLoading(false);
          return;
        }
        const photos = await listPhotos(pairId);
        if (!isCancelled && photos.length > 0) {
          const randomIndex = Math.floor(Math.random() * photos.length);
          setPhoto(photos[randomIndex]);
        }
      } catch (error) {
        console.error("Error fetching random photo:", error);
      } finally {
        if (!isCancelled) {
          setLoading(false);
        }
      }
    }

    fetchRandomPhoto();
    return () => { isCancelled = true; };
  }, []);

  // Always render the card structure to prevent layout shift
  return (
    <div className="card">
      <div className="flex items-center justify-between mb-2">
        <h3 className="text-sm text-gray-600">Foto del día</h3>
        {!loading && photo && (
          <Link to="/gallery" className="btn-link text-sm">Ver galería</Link>
        )}
      </div>

      <div className="relative aspect-square bg-gray-100 rounded-lg overflow-hidden">
        {/* Loading skeleton - always present but fades out */}
        <div className={`absolute inset-0 animate-pulse bg-gray-200 rounded-lg transition-opacity duration-300 ${
          loading || !imageLoaded ? 'opacity-100' : 'opacity-0'
        }`} />

        {/* Image - fades in when loaded */}
        {!loading && photo && (
          <Link to={`/gallery?photo=${photo.id}`} className="block w-full h-full">
            <img 
              src={photo.thumbUrl}
              alt="Recuerdo aleatorio"
              className={`w-full h-full object-cover hover:scale-105 transition-all duration-300 ${
                imageLoaded ? 'opacity-100' : 'opacity-0'
              }`}
              onLoad={() => setImageLoaded(true)}
              onError={() => setImageLoaded(true)} // Show even if image fails to load
            />
          </Link>
        )}

        {/* No photos message */}
        {!loading && !photo && (
          <div className="absolute inset-0 flex items-center justify-center text-gray-500 text-sm">
            No hay fotos aún
          </div>
        )}
      </div>
    </div>
  );
}
