import React, { useEffect, useMemo, useState } from 'react';
import { Routes, Route, Link, useLocation, Navigate } from 'react-router-dom';
import Dashboard from './pages/Dashboard';
import Gallery from './pages/Gallery';
import CalendarPage from './pages/Calendar';
import MapPage from './pages/Map';
import NavBar from './components/NavBar';

const IDENTITY_KEY = 'identity'; // 'yo' | 'ella'

function IdentityGate({ children }) {
  const [identity, setIdentity] = useState(() => localStorage.getItem(IDENTITY_KEY) || '');

  function choose(id) {
    localStorage.setItem(IDENTITY_KEY, id);
    setIdentity(id);
  }

  if (!identity) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-gradient-to-b from-rose-50 to-white p-6">
        <div className="w-full max-w-sm bg-white rounded-2xl shadow-md p-6 text-center">
          <h1 className="text-2xl font-semibold text-rose-600 mb-1">Hola ✨</h1>
          <p className="text-gray-600 mb-4">¿Quién eres?</p>
          <div className="grid grid-cols-2 gap-3">
            <button onClick={() => choose('yo')} className="py-3 rounded-xl bg-rose-500 text-white font-medium active:scale-[0.98]">Yo</button>
            <button onClick={() => choose('ella')} className="py-3 rounded-xl bg-rose-100 text-rose-600 font-medium active:scale-[0.98]">Ella</button>
          </div>
          <p className="text-xs text-gray-400 mt-4">Se guarda solo en tu dispositivo.</p>
        </div>
      </div>
    );
  }

  return <>{children}</>;
}

export default function App() {
  const location = useLocation();
  const title = useMemo(() => {
    switch (location.pathname) {
      case '/': return 'Inicio';
      case '/gallery': return 'Galería';
      case '/calendar': return 'Calendario';
      case '/map': return 'Mapa';
      default: return '';
    }
  }, [location.pathname]);

  return (
    <IdentityGate>
      <div className="min-h-screen bg-rose-50/50 text-gray-900 flex flex-col">
        <header className="sticky top-0 z-10 backdrop-blur bg-white/70 border-b border-rose-100">
          <div className="max-w-screen-md mx-auto px-4 h-14 flex items-center justify-between">
            <span className="font-semibold text-rose-600">Nosotros</span>
            <span className="text-sm text-gray-500">{title}</span>
            <Link to="/settings" className="text-rose-500">⚙️</Link>
          </div>
        </header>

        <main className="flex-1 max-w-screen-md mx-auto w-full px-4 pb-20 pt-4">
          <Routes>
            <Route path="/" element={<Dashboard />} />
            <Route path="/gallery" element={<Gallery />} />
            <Route path="/calendar" element={<CalendarPage />} />
            <Route path="/map" element={<MapPage />} />
            <Route path="/settings" element={<Settings />} />
            <Route path="*" element={<Navigate to="/" replace />} />
          </Routes>
        </main>

        <NavBar />
      </div>
    </IdentityGate>
  );
}

function Settings() {
  const [anniv, setAnniv] = useState(() => localStorage.getItem('anniversary') || '');

  useEffect(() => {
    if (anniv) localStorage.setItem('anniversary', anniv);
  }, [anniv]);

  return (
    <div className="space-y-4">
      <h2 className="text-lg font-semibold text-rose-600">Ajustes</h2>
      <div className="bg-white rounded-2xl p-4 shadow-sm border border-rose-100">
        <label className="block text-sm text-gray-600 mb-1">Fecha de aniversario</label>
        <input type="date" value={anniv} onChange={(e) => setAnniv(e.target.value)}
               className="w-full rounded-xl border border-rose-200 px-3 py-2 outline-none focus:ring-2 focus:ring-rose-300" />
        <p className="text-xs text-gray-500 mt-2">Se usa para contar días y marcar en el calendario.</p>
      </div>
      <IdentityReset />
    </div>
  );
}

function IdentityReset() {
  const [id, setId] = useState(localStorage.getItem('identity') || 'yo');
  return (
    <div className="bg-white rounded-2xl p-4 shadow-sm border border-rose-100">
      <div className="flex items-center justify-between">
        <div>
          <div className="text-sm text-gray-600">Identidad</div>
          <div className="text-gray-900 font-medium">{id === 'yo' ? 'Yo' : 'Ella'}</div>
        </div>
        <div className="flex gap-2">
          <button onClick={() => { localStorage.setItem('identity', 'yo'); setId('yo'); }} className="px-3 py-2 rounded-lg bg-rose-100 text-rose-600">Yo</button>
          <button onClick={() => { localStorage.setItem('identity', 'ella'); setId('ella'); }} className="px-3 py-2 rounded-lg bg-rose-100 text-rose-600">Ella</button>
        </div>
      </div>
    </div>
  );
}
