import React, { useEffect, useMemo, useState } from 'react';
import { listEvents, addEvent, deleteEvent } from '../lib/calendar';
import ViewSwitcher from '../components/ViewSwitcher';
import MonthlyCalendarView from '../components/MonthlyCalendarView';

export default function CalendarPage() {
  const [view, setView] = useState('Lista'); // 'Lista' | 'Calendario'
  const pairId = useMemo(() => localStorage.getItem('pairId') || '', []);
  const identity = useMemo(() => localStorage.getItem('identity') || 'yo', []);
  const [items, setItems] = useState([]);
  const [loading, setLoading] = useState(true);
  const [saving, setSaving] = useState(false);
  const [error, setError] = useState('');

  useEffect(() => {
    let cancelled = false;
    (async () => {
      setLoading(true);
      try {
        const list = await listEvents(pairId, { futureOnly: false, max: 100 });
        if (!cancelled) setItems(list.sort((a,b) => (a.start?.toMillis?.()||0) - (b.start?.toMillis?.()||0)));
      } catch (e) {
        if (!cancelled) setError('Sin conexión o sin permisos.');
      } finally {
        if (!cancelled) setLoading(false);
      }
    })();
    return () => { cancelled = true; };
  }, [pairId]);

  async function onAdd(e) {
    e.preventDefault();
    if (!pairId) return;
    const fd = new FormData(e.currentTarget);
    const title = (fd.get('title') || '').toString();
    const date = (fd.get('date') || '').toString();
    const time = (fd.get('time') || '').toString();
    const location = (fd.get('location') || '').toString();
    if (!title || !date) return;
    setSaving(true); setError('');
    try {
      const ev = await addEvent(pairId, { title, date, time, location }, identity);
      setItems((prev) => [...prev, ev].sort((a,b) => (a.start?.toMillis?.()||0) - (b.start?.toMillis?.()||0)));
      e.currentTarget.reset();
    } catch (e2) {
      setError('No se pudo guardar (offline o permisos). Se intentará al estar online.');
    } finally {
      setSaving(false);
    }
  }

  async function onDelete(id) {
    try {
      await deleteEvent(pairId, id);
      setItems((prev) => prev.filter((x) => x.id !== id));
    } catch {}
  }

  return (
    <div className="space-y-4">
      <ViewSwitcher 
        views={['Lista', 'Calendario']}
        activeView={view}
        onChange={setView}
      />

      {view === 'Calendario' && <MonthlyCalendarView events={events} />}

      {view === 'Lista' && (
      <>
      <div className="card">
        <h3 className="font-semibold">Calendario</h3>
        <p className="text-gray-600">Añade eventos con fecha y hora opcional. Offline-first con sincronización al volver a estar online.</p>
        {error && <p className="text-xs text-rose-600 mt-2">{error}</p>}
      </div>

      <form onSubmit={onAdd} className="card space-y-3">
        <div className="grid grid-cols-1 sm:grid-cols-2 gap-3">
          <input name="title" placeholder="Título" className="input" />
          <input name="location" placeholder="Ubicación (opcional)" className="input" />
          <input name="date" type="date" className="input" />
          <input name="time" type="time" className="input" />
        </div>
        <button disabled={saving} className="btn-primary disabled:opacity-60">{saving ? 'Guardando…' : 'Añadir'}</button>
      </form>

      <div className="card">
        <h4 className="font-medium mb-2">Próximos y pasados</h4>
        {loading ? (
          <div className="text-gray-500">Cargando…</div>
        ) : items.length === 0 ? (
          <div className="text-gray-500">Aún no hay eventos.</div>
        ) : (
          <ul className="divide-y divide-rose-100">
            {items.map((ev) => {
              const d = ev.start?.toDate?.() || null;
              const when = d ? d.toLocaleString() : '';
              return (
                <li key={ev.id} className="py-2 flex items-center gap-3">
                  <div className="flex-1 min-w-0">
                    <div className="font-medium text-gray-900 truncate">{ev.title}</div>
                    <div className="text-xs text-gray-500 truncate">{when}{ev.location ? ` · ${ev.location}` : ''}</div>
                  </div>
                  <button onClick={() => onDelete(ev.id)} className="btn-link text-sm">Borrar</button>
                </li>
              );
            })}
          </ul>
        )}
      </div>
      </>
      )}
    </div>
  );
}
