import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { listPhotos } from '../lib/photos';

export default function RandomPhoto() {
  const [photo, setPhoto] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    let isCancelled = false;
    async function fetchRandomPhoto() {
      try {
        const pairId = localStorage.getItem('pairId');
        if (!pairId) {
          setLoading(false);
          return;
        }
        const photos = await listPhotos(pairId);
        if (!isCancelled && photos.length > 0) {
          const randomIndex = Math.floor(Math.random() * photos.length);
          setPhoto(photos[randomIndex]);
        }
      } catch (error) {
        console.error("Error fetching random photo:", error);
      } finally {
        if (!isCancelled) {
          setLoading(false);
        }
      }
    }

    fetchRandomPhoto();
    return () => { isCancelled = true; };
  }, []);

  if (loading) {
    return (
      <div className="card aspect-square animate-pulse bg-gray-200"></div>
    );
  }

  if (!photo) {
    return null; // Don't show anything if there are no photos
  }

  return (
    <div className="card p-0 overflow-hidden">
      <Link to={`/gallery?photo=${photo.id}`} className="block w-full h-full aspect-square bg-gray-100">
        <img 
          src={photo.thumbUrl}
          alt="Recuerdo aleatorio"
          className="w-full h-full object-cover"
        />
      </Link>
    </div>
  );
}
