import React, { useEffect, useMemo, useState } from 'react';
import { Link } from 'react-router-dom';

export default function Dashboard() {
  const [anniv, setAnniv] = useState(() => localStorage.getItem('anniversary'));

  const daysTogether = useMemo(() => {
    if (!anniv) return null;
    const start = new Date(anniv);
    const now = new Date();
    const diff = Math.floor((now - start) / (1000 * 60 * 60 * 24));
    return diff;
  }, [anniv]);

  useEffect(() => {
    const onStorage = () => setAnniv(localStorage.getItem('anniversary'));
    window.addEventListener('storage', onStorage);
    return () => window.removeEventListener('storage', onStorage);
  }, []);

  return (
    <div className="space-y-4">
      {daysTogether !== null ? (
        <div className="card">
          <div className="text-sm text-gray-600">Llevamos</div>
          <div className="text-3xl font-semibold text-rose-600">{daysTogether} d√≠as</div>
        </div>
      ) : (
        <div className="card">
          <div className="text-sm text-gray-600">Configura tu aniversario en Ajustes</div>
          <Link to="/settings" className="inline-block mt-2 btn-primary">Ir a Ajustes</Link>
        </div>
      )}

      <div className="card">
        <div className="flex items-center justify-between mb-2">
          <h3 className="font-semibold text-gray-900">Pr√≥ximos eventos</h3>
          <Link to="/calendar" className="btn-link text-sm">Ver calendario</Link>
        </div>
        <p className="text-gray-500">A√∫n no hay eventos. ¬°Crea el primero!</p>
      </div>

      <div className="grid grid-cols-2 gap-3">
        <Link to="/gallery" className="block rounded-2xl bg-gradient-to-br from-rose-100 to-white border border-rose-200 p-4 text-center transition hover:shadow-sm hover:border-rose-300">üì∑<div className="mt-1 text-sm">Galer√≠a</div></Link>
        <Link to="/map" className="block rounded-2xl bg-gradient-to-br from-rose-100 to-white border border-rose-200 p-4 text-center transition hover:shadow-sm hover:border-rose-300">üó∫Ô∏è<div className="mt-1 text-sm">Mapa</div></Link>
      </div>
    </div>
  );
}
