import { useEffect, useState } from 'react';

export default function HeartRainAnimation({ isActive, duration = 3500, type = 'rain' }) {
  const [hearts, setHearts] = useState([]);

  useEffect(() => {
    if (!isActive) {
      setHearts([]);
      return;
    }

    let newHearts = [];

    if (type === 'fireworks') {
      // Fireworks animation: hearts explode from center
      const heartCount = 30;
      newHearts = Array.from({ length: heartCount }, (_, i) => {
        const angle = (i / heartCount) * 2 * Math.PI;
        const distance = 150 + Math.random() * 200; // Random distance from center
        const centerX = 50; // Center of screen
        const centerY = 50;

        return {
          id: i,
          left: centerX + Math.cos(angle) * (distance / 10), // Convert to percentage
          top: centerY + Math.sin(angle) * (distance / 10),
          delay: Math.random() * 500, // Shorter delay for fireworks
          size: Math.random() * 1 + 1.2, // Larger hearts (1.2-2.2rem)
          emoji: ['ðŸ’–', 'ðŸ’•', 'ðŸ’—', 'â¤ï¸', 'ðŸ’', 'ðŸŽ†', 'âœ¨'][Math.floor(Math.random() * 7)],
          animationType: 'fireworks',
        };
      });
    } else {
      // Rain animation: hearts fall from top
      const heartCount = 25;
      newHearts = Array.from({ length: heartCount }, (_, i) => ({
        id: i,
        left: Math.random() * 100, // Random horizontal position (0-100%)
        delay: Math.random() * 2000, // Longer delay for more spread
        size: Math.random() * 0.8 + 1, // Random size (1-1.8rem)
        emoji: ['ðŸ’–', 'ðŸ’•', 'ðŸ’—', 'â¤ï¸', 'ðŸ’'][Math.floor(Math.random() * 5)],
        animationType: 'rain',
      }));
    }

    setHearts(newHearts);

    // Clear hearts after animation duration
    const timer = setTimeout(() => {
      setHearts([]);
    }, duration);

    return () => clearTimeout(timer);
  }, [isActive, duration, type]);

  if (!isActive || hearts.length === 0) return null;

  return (
    <>
      <style>{`
        @keyframes heartFall {
          0% {
            transform: translateY(-50px) rotate(0deg);
            opacity: 1;
          }
          100% {
            transform: translateY(calc(100vh + 100px)) rotate(360deg);
            opacity: 0;
          }
        }
        @keyframes heartFireworks {
          0% {
            transform: translate(0, 0) scale(0.5) rotate(0deg);
            opacity: 1;
          }
          50% {
            transform: translate(var(--dx), var(--dy)) scale(1.2) rotate(180deg);
            opacity: 1;
          }
          100% {
            transform: translate(calc(var(--dx) * 1.5), calc(var(--dy) * 1.5)) scale(0.3) rotate(360deg);
            opacity: 0;
          }
        }
        .heart-fall {
          animation: heartFall 4s ease-in forwards;
        }
        .heart-fireworks {
          animation: heartFireworks 3s ease-out forwards;
        }
      `}</style>
      <div className="fixed inset-0 pointer-events-none z-50 overflow-hidden">
        {hearts.map((heart) => {
          if (heart.animationType === 'fireworks') {
            const dx = (heart.left - 50) * 8; // Distance from center
            const dy = (heart.top - 50) * 8;

            return (
              <div
                key={heart.id}
                className="absolute heart-fireworks"
                style={{
                  left: '50%',
                  top: '50%',
                  fontSize: `${heart.size}rem`,
                  animationDelay: `${heart.delay}ms`,
                  '--dx': `${dx}px`,
                  '--dy': `${dy}px`,
                }}
              >
                {heart.emoji}
              </div>
            );
          } else {
            return (
              <div
                key={heart.id}
                className="absolute heart-fall"
                style={{
                  left: `${heart.left}%`,
                  top: '-50px',
                  fontSize: `${heart.size}rem`,
                  animationDelay: `${heart.delay}ms`,
                }}
              >
                {heart.emoji}
              </div>
            );
          }
        })}
      </div>
    </>
  );
}
