import React, { useEffect, useState } from 'react';
import { auth, db, authReady } from '../lib/firebase';

function haversineKm(a, b) {
  const R = 6371;
  const dLat = (b.lat - a.lat) * Math.PI / 180;
  const dLon = (b.lng - a.lng) * Math.PI / 180;
  const lat1 = a.lat * Math.PI / 180;
  const lat2 = b.lat * Math.PI / 180;
  const x = Math.sin(dLat/2)**2 + Math.cos(lat1) * Math.cos(lat2) * Math.sin(dLon/2)**2;
  return 2 * R * Math.asin(Math.sqrt(x));
}

export default function MapPage() {
  const [me, setMe] = useState(null);
  const [partner, setPartner] = useState(() => {
    const saved = localStorage.getItem('partnerLocation');
    return saved ? JSON.parse(saved) : null;
  });
  const identity = (typeof localStorage !== 'undefined' && localStorage.getItem('identity')) || 'yo';
  const partnerIdentity = identity === 'yo' ? 'ella' : 'yo';

  useEffect(() => {
    if (!('geolocation' in navigator)) return;
    navigator.geolocation.getCurrentPosition(async (pos) => {
      const coords = { lat: pos.coords.latitude, lng: pos.coords.longitude };
      setMe(coords);
      localStorage.setItem('myLocation', JSON.stringify(coords));
      // Push my location once to Firestore (no tiempo real)
      try {
        const pairId = localStorage.getItem('pairId') || '';
        if (!pairId || !db) return;
        if (authReady) await authReady;
        if (!auth?.currentUser) return;
        const { doc, setDoc, collection, serverTimestamp } = await import('firebase/firestore');
        await setDoc(
          doc(collection(db, 'pairs', pairId, 'locations'), identity),
          { lat: coords.lat, lng: coords.lng, updatedAt: serverTimestamp(), uid: auth.currentUser.uid },
          { merge: true }
        );
      } catch (e) {
        // offline o permisos; ignorar: ya persistimos localmente
      }
    }, console.error, { enableHighAccuracy: true, maximumAge: 60_000, timeout: 10_000 });
  }, []);

  useEffect(() => {
    // Fetch partner location once from Firestore
    let cancelled = false;
    (async () => {
      try {
        const pairId = localStorage.getItem('pairId') || '';
        if (!pairId || !db) return;
        if (authReady) await authReady;
        const { doc, getDoc, collection } = await import('firebase/firestore');
        const snap = await getDoc(doc(collection(db, 'pairs', pairId, 'locations'), partnerIdentity));
        if (!cancelled && snap.exists()) {
          const data = snap.data();
          if (typeof data.lat === 'number' && typeof data.lng === 'number') {
            const coords = { lat: data.lat, lng: data.lng };
            setPartner(coords);
            localStorage.setItem('partnerLocation', JSON.stringify(coords));
          }
        }
      } catch (e) {
        // offline o doc ausente: mantenemos el fallback local
      }
    })();
    return () => { cancelled = true; };
  }, [partnerIdentity]);

  const distance = me && partner ? haversineKm(me, partner) : null;

  return (
    <div className="space-y-4">
      <div className="card">
        <h3 className="font-semibold mb-1">Distancia</h3>
        {distance !== null ? (
          <div className="text-3xl font-semibold text-rose-600">{distance.toFixed(1)} km</div>
        ) : (
          <p className="text-gray-600">Esperando ubicaciones...</p>
        )}
        <div className="flex items-center gap-2 mt-2">
          <button
            className="btn-ghost text-sm"
            onClick={() => {
              // Manual refresh partner location
              (async () => {
                try {
                  const pairId = localStorage.getItem('pairId') || '';
                  if (!pairId || !db) return;
                  if (authReady) await authReady;
                  const { doc, getDoc, collection } = await import('firebase/firestore');
                  const snap = await getDoc(doc(collection(db, 'pairs', pairId, 'locations'), partnerIdentity));
                  if (snap.exists()) {
                    const data = snap.data();
                    if (typeof data.lat === 'number' && typeof data.lng === 'number') {
                      const coords = { lat: data.lat, lng: data.lng };
                      setPartner(coords);
                      localStorage.setItem('partnerLocation', JSON.stringify(coords));
                    }
                  }
                } catch {}
              })();
            }}
          >Actualizar</button>
          <p className="text-xs text-gray-500">Actualización única al entrar. Usa “Actualizar” para reconsultar.</p>
        </div>
      </div>

      <div className="card space-y-2">
        <h4 className="font-medium">Ubicación de tu pareja</h4>
        <p className="text-sm text-gray-600">Pídele que te envíe sus coordenadas (lat,lng) por WhatsApp y pégalas aquí de momento.</p>
        <form onSubmit={(e) => { e.preventDefault(); const v = e.currentTarget.coords.value.trim(); const [lat, lng] = v.split(',').map(Number); if (!isFinite(lat) || !isFinite(lng)) return; const p = { lat, lng }; setPartner(p); localStorage.setItem('partnerLocation', JSON.stringify(p)); e.currentTarget.reset(); }}>
          <input name="coords" placeholder="41.4036,2.1744" className="input w-full" />
          <button className="mt-2 w-full btn-primary">Guardar</button>
        </form>
      </div>
    </div>
  );
}
