import { useEffect, useState } from 'react';

export default function HeartRainAnimation({ isActive, duration = 2000 }) {
  const [hearts, setHearts] = useState([]);

  useEffect(() => {
    if (!isActive) {
      setHearts([]);
      return;
    }

    // Create multiple hearts with random positions and delays
    const heartCount = 20;
    const newHearts = Array.from({ length: heartCount }, (_, i) => ({
      id: i,
      left: Math.random() * 100, // Random horizontal position (0-100%)
      delay: Math.random() * 1500, // Random delay (0-1500ms)
      size: Math.random() * 0.8 + 1, // Random size (1-1.8rem)
      emoji: ['💖', '💕', '💗', '❤️', '💝'][Math.floor(Math.random() * 5)], // Random heart emoji
    }));

    setHearts(newHearts);

    // Clear hearts after animation duration
    const timer = setTimeout(() => {
      setHearts([]);
    }, duration);

    return () => clearTimeout(timer);
  }, [isActive, duration]);

  if (!isActive || hearts.length === 0) return null;

  return (
    <>
      <style>{`
        @keyframes heartFall {
          0% {
            transform: translateY(-50px) rotate(0deg);
            opacity: 1;
          }
          100% {
            transform: translateY(calc(100vh + 50px)) rotate(360deg);
            opacity: 0;
          }
        }
        .heart-fall {
          animation: heartFall 2.5s ease-in forwards;
        }
      `}</style>
      <div className="fixed inset-0 pointer-events-none z-50 overflow-hidden">
        {hearts.map((heart) => (
          <div
            key={heart.id}
            className="absolute heart-fall"
            style={{
              left: `${heart.left}%`,
              top: '-50px',
              fontSize: `${heart.size}rem`,
              animationDelay: `${heart.delay}ms`,
            }}
          >
            {heart.emoji}
          </div>
        ))}
      </div>
    </>
  );
}
