import React, { useState } from 'react';

export default function Gallery() {
  const [files, setFiles] = useState([]);

  function onSelect(e) {
    const list = Array.from(e.target.files || []);
    setFiles(list.map(f => ({ file: f, url: URL.createObjectURL(f) })));
  }

  return (
    <div className="space-y-4">
      <div className="bg-white rounded-2xl p-4 border border-rose-100 shadow-sm">
        <h3 className="font-semibold mb-2">Subir fotos</h3>
        <input type="file" accept="image/*" multiple onChange={onSelect} />
        <p className="text-xs text-gray-500 mt-2">Pr√≥ximamente: guardado en la nube (Firebase Storage).</p>
      </div>

      <div className="grid grid-cols-3 gap-2">
        {files.map(({ url }, i) => (
          <img key={i} src={url} alt="preview" className="w-full h-28 object-cover rounded-xl border border-rose-100" />
        ))}
      </div>
    </div>
  );
}
