import React from 'react';
import { Link, useLocation } from 'react-router-dom';

const tabs = [
  { to: '/', label: 'Inicio', icon: 'üè†' },
  { to: '/gallery', label: 'Galer√≠a', icon: 'üì∑' },
  { to: '/calendar', label: 'Calendario', icon: 'üìÖ' },
  { to: '/map', label: 'Mapa', icon: 'üó∫Ô∏è' },
];

export default function NavBar() {
  const loc = useLocation();
  return (
    <nav className="fixed bottom-0 inset-x-0 z-10 border-t border-rose-100 bg-white/90 backdrop-blur">
      <div className="max-w-screen-md mx-auto grid grid-cols-4">
        {tabs.map(t => {
          const active = loc.pathname === t.to;
          return (
            <Link
              key={t.to}
              to={t.to}
              aria-label={t.label}
              className={`flex flex-col items-center justify-center h-16 text-sm ${active ? 'text-rose-600 font-medium' : 'text-gray-500'}`}
            >
              <span className="text-xl leading-none">{t.icon}</span>
              <span className="text-[11px] mt-0.5">{t.label}</span>
            </Link>
          );
        })}
      </div>
    </nav>
  );
}
