import React, { useState, useMemo } from 'react';

const monthNames = ["Enero", "Febrero", "Marzo", "Abril", "Mayo", "Junio", "Julio", "Agosto", "Septiembre", "Octubre", "Noviembre", "Diciembre"];
const dayNames = ['D', 'L', 'M', 'X', 'J', 'V', 'S'];

export default function MonthlyCalendarView({ events = [] }) {
  const [currentDate, setCurrentDate] = useState(new Date());

  const eventsByDate = useMemo(() => {
    const map = new Map();
    events.forEach(event => {
      if (event.start && event.start.toDate) {
        const date = event.start.toDate();
        const dateString = `${date.getFullYear()}-${date.getMonth()}-${date.getDate()}`;
        if (!map.has(dateString)) {
          map.set(dateString, []);
        }
        map.get(dateString).push(event);
      }
    });
    return map;
  }, [events]);

  const year = currentDate.getFullYear();
  const month = currentDate.getMonth();

  const firstDayOfMonth = (new Date(year, month, 1).getDay() + 6) % 7;
  const daysInMonth = new Date(year, month + 1, 0).getDate();

  const handlePrevMonth = () => {
    setCurrentDate(new Date(year, month - 1, 1));
  };

  const handleNextMonth = () => {
    setCurrentDate(new Date(year, month + 1, 1));
  };

  const calendarDays = [];
  // Padding for previous month
  for (let i = 0; i < firstDayOfMonth; i++) {
    calendarDays.push(<div key={`pad-start-${i}`} className="p-2"></div>);
  }
  // Days of current month
  for (let day = 1; day <= daysInMonth; day++) {
    const date = new Date(year, month, day);
    const isToday = date.toDateString() === new Date().toDateString();
    const dateString = `${date.getFullYear()}-${date.getMonth()}-${date.getDate()}`;
    const hasEvents = eventsByDate.has(dateString);

    calendarDays.push(
      <div key={`day-${day}`} className="p-2 text-center border border-gray-200/80 rounded-lg h-12 flex items-center justify-center relative">
        <span className={`w-8 h-8 flex items-center justify-center rounded-full ${isToday ? 'bg-rose-500 text-white' : ''}`}>
          {day}
        </span>
        {hasEvents && (
          <div className="absolute bottom-1.5 left-1/2 -translate-x-1/2 w-4 h-1 bg-rose-500 rounded-full"></div>
        )}
      </div>
    );
  }

  return (
    <div className="card">
      <div className="flex items-center justify-between mb-4">
        <button onClick={handlePrevMonth} className="btn-ghost p-2 rounded-full w-10 h-10 flex items-center justify-center text-xl">‹</button>
        <h3 className="font-semibold text-lg text-center">{monthNames[month]} {year}</h3>
        <button onClick={handleNextMonth} className="btn-ghost p-2 rounded-full w-10 h-10 flex items-center justify-center text-xl">›</button>
      </div>
      <div className="grid grid-cols-7 gap-2 text-center text-sm font-medium text-gray-500 mb-2">
        {dayNames.slice(1).map(day => <div key={day}>{day}</div>)}
        <div>{dayNames[0]}</div>
      </div>
      <div className="grid grid-cols-7 gap-1">
        {calendarDays}
      </div>
    </div>
  );
}
